import logo from './logo.svg';
import './App.css';
import { Login } from './Login';
import { useState, useEffect } from 'react';
import React from 'react'
import axios from 'axios';

export const TodosList = () => {
    const [todos, setTodos] = useState([])
    useEffect(() => {
        // add try catch later
        const getTodos = async () => {
            const res = await axios.get("https://localhost:44353/api/TodoTask")
            setTodos(res.data)
        }

        getTodos()

    }, [])
    return (<div>
      Todos
      {todos.map(t => {
        return <div>
            Task Name: {t.taskName} <br></br>
            Task Description: {t.taskDescription} <br></br>
            IsCompleted? <checkbox value={t.taskStatus}></checkbox>

            <div>
                Task Name:
                <input value={t.taskName} />
                Password
                <input value={password} onChange={e => setPassword(e?.target.value)} />
                <button onClick={async () => {
                console.log(userName, password)
                try {
                    const res = await axios.post('https://localhost:44353/api/Authentication/login', {
                        Username: userName,
                        Password: password
                    })
                    onLoggedIn(res.data)
                } catch(err) {
                    // handle error
                }
                
                }}>Submit</button>
            </div>

        </div>
      })}

      <button onClick={() => {}}>Add todo</button>
    </div>)
}
