import logo from './logo.svg';
import './App.css';
import { Login } from './Login';
import { useState, useEffect } from 'react';
import React from 'react'
import axios from 'axios';

export const TodosList = () => {
    const [todos, setTodos] = useState([])

    const [taskName, setTaskName] = useState('')
    const [taskDescription, setTaskDescription] = useState('')
    
    useEffect(() => {
        // add try catch later
        const getTodos = async () => {
            const res = await axios.get("https://localhost:44353/api/TodoTask")
            setTodos(res.data)
        }

        getTodos()

    }, [])
    return (<div>
      <h1>Todos</h1>
      {todos.map(t => {
        return <div>
            Task Id: {t.taskId} <br></br>
            Task Name: {t.taskName} <br></br>
            Task Description: {t.taskDescription} <br></br>
            IsCompleted? <checkbox value={t.taskStatus}></checkbox>
            <br></br>
            <br></br>
        </div>
      })}      

      <button onClick={() => {}}>Add todo</button>
    </div>)
}

export const CreateTodos = ({createNewTask}) => {
    const [todos, setTodos] = useState([])

    const [taskName, setTaskName] = useState('')
    const [taskDescription, setTaskDescription] = useState('')
    
    useEffect(() => {
        // add try catch later
        const CreateTodos = async () => {
            const res = await axios.get("https://localhost:44353/api/TodoTask")
            setTodos(res.data)
        }

        CreateTodos()

    }, [])
    return (<div>
      <h1>Todos</h1>
      {todos.map(t => {
        return <div>
            Task Id: {t.taskId} <br></br>
            Task Name: {t.taskName} <br></br>
            Task Description: {t.taskDescription} <br></br>
            IsCompleted? <checkbox value={t.taskStatus}></checkbox>
            <br></br>
            <br></br>

            <div>
                <h2>Please Add New Task Below</h2>
                    Task Name:
                    <input value={taskName} />
                    Task Description:
                    <input value={taskDescription} />
                    <button onClick={async () => {
                    console.log(taskName, taskDescription)
                    try {
                        const res = await axios.post('https://localhost:44353/api/TodoTask', {
                            TaskName: taskName,
                            TaskDescription: taskDescription
                        })
                        createNewTask(res.data)
                    } catch(err) {
                        // handle error
                    }
                    
                    }}>Submit</button>
            </div>

        </div>
      })}      

      <button onClick={() => {}}>Add todo</button>
    </div>)
}
